import engine.FpSoundEngine;
import setup.LineService;
import setup.MixerService;

import java.io.File;
import java.io.PrintStream;
import java.nio.file.Paths;

/*
 * This Java source file was generated by the Gradle 'init' task.
 */
public class App {

    public static void main(String[] args) {
        MixerService.MixerDetails(MixerService.SystemMixers()).forEach(System.out::println);
        System.out.println("---------------------------------------------------------------");
        MixerService.MixerDetails(MixerService.AppropriateMixers()).forEach(System.out::println);
        System.out.println("---------------------------------------------------------------");
        LineService.SystemSourceDataLine().ifPresent(line -> System.out.println(LineService.LineDetails(line)));

        FpSoundEngine seg = new FpSoundEngine();
        seg.playSound(Paths.get(System.getProperty("user.home"),"/fpAudio/test.wav").toFile());
    }


}
